  //TODO CODE
  public void display(World test) throws BadLocationException {
        for (int i = 0; i < test.world.length; i++) {
            for (int j = 0; j < test.world[0].length; j++) {
                appendString(" ");
                if (test.world[j][i].creature != null && test.world[j][i].creature.creatureType != 0) {
                    appendString("c");
                } else if (test.world[j][i].structure != 0) {
                    appendString("s");
                } else if (test.world[j][i].item[0] != 0) {
                    appendString("i");
                } else {
                    appendString("" + D.stringifyLand(test.world[j][i].landType));
                }
                appendString(" ");
            }
            appendString("\n");
        }
    }

    public void appendString(String str) throws BadLocationException {
        StyledDocument document = (StyledDocument) MapDisplay.getDocument();
        Style blank = MapDisplay.addStyle("land", null);
        StyleConstants.setForeground(blank, Color.white);
        Style water = MapDisplay.addStyle("land", null);
        StyleConstants.setForeground(water, Color.blue);
        Style land = MapDisplay.addStyle("land", null);
        Color dkGreen = Color.green.darker();
        StyleConstants.setForeground(land, Color.green.darker());
        
        switch (str) {
            case "w":
                //str+=" ";
                document.insertString(document.getLength(), str, water);
                break;
            case "l":
                //str+="    ";
                document.insertString(document.getLength(), str, land);
                break;
            case "~":
                //str+="   ";
                document.insertString(document.getLength(), str, null);
                break;
            default:
                document.insertString(document.getLength(), str, null);
                break;
        }
    }
